import globals from 'globals'
import { PKG_NAME_PREFIX } from '../globs'

/** @see https://dev.to/ajmal_hasan/eslint-prettier-setup-for-latest-react-native-with-typescript-17do */
export async function reactNative() {
  const pluginReactNative = (await import('eslint-plugin-react-native')).default

  return [
    {
      name: `${PKG_NAME_PREFIX}/react-native`,
      files: ['**/*.{js,mjs,cjs,ts,mts,cts,jsx,tsx}'],
      languageOptions: {
        globals: {
          ...globals.browser,
          ...globals.node,
          __DEV__: 'readonly',
          __dirname: 'readonly',
          __filename: 'readonly',
        },
        ecmaVersion: 'latest',
        sourceType: 'module',
        parserOptions: {
          ecmaFeatures: { jsx: true },
        },
      },
      plugins: {
        'react-native': pluginReactNative,
      },
      rules: {
      // React Native
        'react-native/no-unused-styles': 'error',
        'react-native/split-platform-components': 'error',
        'react-native/no-inline-styles': 'warn',
        'react-native/no-color-literals': 'warn',
        'react-native/no-single-element-style-arrays': 'error',

        // React Hooks
        'react-hooks/rules-of-hooks': 'error',
        'react-hooks/exhaustive-deps': 'off', // Disabled for ESLint 9 compatibility

        // React
        'react/react-in-jsx-scope': 'off',
        'react/prop-types': 'off',
        'react/display-name': 'off',

        // General
        'no-console': 'warn',
        'no-debugger': 'error',
        'no-unused-vars': 'off',
        'prefer-const': 'error',
        'no-var': 'error',
        'no-duplicate-imports': 'error',
        'max-lines': ['warn', { max: 300, skipBlankLines: true }],
      },
      settings: {
        react: { version: 'detect' },
      },
    },
  ]
}
